mapper.select:
SELECT id, customer_id, status, country, created_at
FROM accounts
WHERE id = #{id}

SELECT id, customer_id, status, country, created_at
FROM accounts
WHERE customer_id = #{customerId}

mapper.update:
UPDATE accounts
SET
    customer_id = #{customerId},
    status = #{accountStatus},
    country = #{country}
WHERE id = #{id}
